CREATE TABLE grdb_migrations (identifier TEXT NOT NULL PRIMARY KEY);
CREATE TABLE IF NOT EXISTS "state" ("id" TEXT PRIMARY KEY, "data" BLOB NOT NULL);
CREATE TABLE IF NOT EXISTS "action" ("id" TEXT PRIMARY KEY, "data" BLOB NOT NULL);
CREATE TABLE IF NOT EXISTS "link" ("id" TEXT PRIMARY KEY, "url" TEXT NOT NULL COLLATE NOCASE, "originalURL" TEXT COLLATE NOCASE, "title" TEXT COLLATE NOCASE, "summary" TEXT COLLATE NOCASE, "author" TEXT COLLATE NOCASE, "preview" TEXT COLLATE NOCASE, "tags" TEXT COLLATE NOCASE, "starred" BOOLEAN NOT NULL, "readAt" DOUBLE NOT NULL, "addedAt" DOUBLE NOT NULL, "modifiedAt" DOUBLE NOT NULL, "fetchStatus" INTEGER NOT NULL, "status" INTEGER NOT NULL, "highlightTotal" INTEGER, "imageDownloaded" BOOLEAN NOT NULL DEFAULT 0, "fileSynced" BOOLEAN NOT NULL DEFAULT 0, "publishedAt" DOUBLE NOT NULL DEFAULT 0, "deletedAt" DOUBLE NOT NULL DEFAULT 0, "authorImage" TEXT);
CREATE TABLE IF NOT EXISTS "content" ("id" TEXT PRIMARY KEY, "content" TEXT COLLATE NOCASE, "wordCount" INTEGER NOT NULL, "videoDuration" INTEGER);
CREATE INDEX "content_on_content" ON "content"("content");
CREATE TABLE IF NOT EXISTS "extra" ("id" TEXT PRIMARY KEY, "info" TEXT COLLATE NOCASE);
CREATE INDEX "extra_on_info" ON "extra"("info");
CREATE TABLE IF NOT EXISTS "highlight" ("id" TEXT PRIMARY KEY, "linkId" TEXT NOT NULL, "content" TEXT NOT NULL COLLATE NOCASE, "parts" TEXT NOT NULL COLLATE NOCASE, "color" INTEGER, "note" TEXT COLLATE NOCASE, "time" DOUBLE NOT NULL, "order" INTEGER NOT NULL, "rtl" BOOLEAN NOT NULL DEFAULT 0, "status" INTEGER NOT NULL);
CREATE INDEX "highlight_on_linkId" ON "highlight"("linkId");
